@app.route('/api/recomendaciones/<A>', methods=['GET'])
def dar_marca_get(A):
	d = {}
	for i in query:
		if i["id"] == int(A):
			d = i
	return jsonify({'recomendaciones': d["suggested"]}), 201

@app.errorhandler(404)
def not_found(error):
    return make_response(jsonify({'error': 'Not found'}), 404)

